import React from "react";
import "./WeatherReport.css";
class WeatherReport extends React.Component{
    constructor(props) {
        super(props);
        

        this.state = {
          error: null,
          isLoaded: false,
          woeid: 2471217,
          items: [],
        };
        this.positionSuccess = this.positionSuccess.bind(this);
        this.positionSuccess();
      }
    positionSuccess(pos){
        if(pos !== undefined){
        let coord = pos.coords
       
            fetch("https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/search/?lattlong=" +coord.latitude + "," + coord.longitude)
        .then(res => res.json)
        .then(
            (result) =>{
                this.setState({
                    woeid: result.woeid
                })
            }
        )    
        }
        
        
    }
    componentDidMount(){

        navigator.geolocation.getCurrentPosition(this.positionSuccess);
         

        fetch("https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/" + this.state.woeid,{
                method: 'GET',
                headers: {
                  'Accept': 'application/json',
                  'Content-Type': 'application/json',
                },
        })
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                  weather: result
              });
            },
            (error) => {
                return <p>Loading your info....</p>
            }
        )
    }
    render(){
         if(this.state.weather !== undefined){
            var options = { weekday: 'long', month: 'long', day: 'numeric' };
             let localDate = new Date(this.state.weather.time).toLocaleDateString("En-Us", options);
    
   
            return(
                <div className = "weatherInfo">
                    <h1 className="update">Your Local Weather Report</h1>
                    <img style={{width: 175, height: 175}} src={"https://cors-anywhere.herokuapp.com/https://www.metaweather.com/static/img/weather/png/" + this.state.weather.consolidated_weather[0].weather_state_abbr + ".png"} />
                    <p >Hmmm... looks like {this.state.weather.consolidated_weather[0].weather_state_name} in {this.state.weather.title}</p>
                    <p >Current Tempature: {Math.round(this.state.weather.consolidated_weather[0].the_temp * 9/5 + 32) } Degrees, Farenheight</p>
                    <p >As Of: {localDate}</p>
                </div>
                
                
            )
            
         }
         return(
             <p>Loading your Weather</p>
         )

    }   

}

export default WeatherReport;
