import React from "react";

class WeatherReport extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
      }
    async componentDidMount(){
        fetch("https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/2430683/")
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                  weather: result
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
                return <p>Loading your info....</p>
            }
        )
        if (this.state.weather.consolidated_weather ===  undefined){
            return <p>Loading your info....</p>
        }  
        alert(this.state.weather.consolidated_weather[0])
        await fetch("https://cors-anywhere.herokuapp.com//https://www.metaweather.com/static/img/weather/png" + this.state.weather.consolidated_weather[0].weather_state_abbr + ".png",{
                method: 'GET',
                headers: {
                  'Accept': 'application/json',
                  'Content-Type': 'application/json',
                },
            }).then((response) =>{
                return response.text();

            }).then((data) =>{
                this.setState({
                    pic : JSON.parse(data)
                })
            },)
            
        
    }
    render(){
         
        return(
        <div>
            <p>Weather Report</p>
            {this.state.pic}
        </div>
        
        
        )

    }   

}

export default WeatherReport;
