import React from "react";
import "./WeatherReport.css";
class WeatherReport extends React.Component{
    constructor(props) {
        super(props);
        

        this.state = {
          error: null,
          isLoaded: false,
          items: [],
          woeid: 0,
          weather: {}
        };
        this.getCoords = this.getCoords.bind(this);
        this.getLocationFromCoords = this.getLocationFromCoords.bind(this);
        this.getWeatherInfo = this.getWeatherInfo.bind(this);
      }

    componentDidMount(){

        if(navigator.geolocation){
            navigator.geolocation.getCurrentPosition(this.getCoords);
        }else{
            this.setState = {
                woeid : 2471217
            }
            getWeatherInfo();
        }
    

        
    }

    getCoords(pos){
        this.setState({
            latitude: pos.coords.latitude,
            longitude: pos.coords.longitude
        })
        getLocationFromCoords();
    }
     getLocationFromCoords(){
        fetch("https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/search/?lattlong=" +this.state.latitude + "," + this.state.longitude)
        .then(res => res.json())
        .then(result => this.setState({woeid : result[0].woeid})
        .then(this.getWeatherInfo()))

        
    }
    getWeatherInfo(){
        if(this.state.woeid){
            fetch("https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/" + this.state.woeid)
            .then(res => res.json())
            .then((result) => {
                this.setState({
                    weather: result
                })
            });

        }
    }

    render(){
    console.log(this.state)
       if(this.state.woeid !== 0){
        var options = { weekday: 'long', month: 'long', day: 'numeric' };
            let localDate = new Date(this.state.weather.time).toLocaleDateString("En-Us", options);
            return(
                <div className = "weatherInfo">
                    <h1 className="update">Your Local Weather Report</h1>
                    <img style={{width: 175, height: 175}} src={"https://www.metaweather.com/static/img/weather/png/" + this.state.weather.consolidated_weather[0].weather_state_abbr + ".png"} />
                    <p >Hmmm... looks like {this.state.weather.consolidated_weather[0].weather_state_name} in {this.state.weather.title}</p>
                    <p >Current Tempature: {Math.round(this.state.weather.consolidated_weather[0].the_temp * 9/5 + 32) } Degrees, Farenheight</p>
                    <p >As Of: {localDate}</p>
                </div>    
            )
       }else{
        return(
            <p>Loading your Weather</p>
        )
       }
    }   

}

export default WeatherReport;

