import React from "react";

class WeatherReport extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
      }
    componentDidMount(){
        fetch("https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/2430683",{
                method: 'GET',
                headers: {
                  'Accept': 'application/json',
                  'Content-Type': 'application/json',
                },
        })
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                  weather: result
              });
            },
            (error) => {
                return <p>Loading your info....</p>
            }
        )
    }
    render(){
        
         if(this.state.weather !== undefined){
            let date = new Date(this.state.weather.time.toString())
            return(
                <div className = "weatherInfo">
                    <p>Your Local Weather Report</p>
                    <img style={{width: 175, height: 175}} src={"https://cors-anywhere.herokuapp.com/https://www.metaweather.com/static/img/weather/png/" + this.state.weather.consolidated_weather[0].weather_state_abbr + ".png"} />
                    <p className= "update">Hmmm... Weather looks like: {this.state.weather.consolidated_weather[0].weather_state_name}</p>
                    <p>{date}</p>
                </div>
                
                
            )
            
         }
         return(
             <p>Loading your Weather</p>
         )

    }   

}

export default WeatherReport;
