import React from "react";

class WeatherReport extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
      }
    componentDidMount(){
        fetch("https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/2430683",{
                method: 'GET',
                headers: {
                  'Accept': 'application/json',
                  'Content-Type': 'application/json',
                },
        })
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                  weather: result
              });
              fetch("https://cors-anywhere.herokuapp.com/https://www.metaweather.com/static/img/weather/png/" + this.state.weather.consolidated_weather[0].weather_state_abbr + ".png",{
                
            }).then((response) =>{
                return response.text();

            }).then((result) =>{
                this.setState({
                    pic : result
                })
            })
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
                return <p>Loading your info....</p>
            }
        )
    }
    render(){
         if(this.state.weather !== undefined){
            return(
                <div>
                    <p>Weather Report</p>
                    <img style={{width: 175, height: 175}} src={"https://cors-anywhere.herokuapp.com/https://www.metaweather.com/static/img/weather/png/" + this.state.weather.consolidated_weather[0].weather_state_abbr + ".png"} />
                </div>
                
                
            )
            
         }
         return(
             <p>Loading your Weather</p>
         )

    }   

}

export default WeatherReport;
