import React from "react";
import "./WeatherReport.css";
class WeatherReport extends React.Component{
    constructor(props) {
        super(props);
     

        this.state = {
          error: null,
          isLoaded: false,
          items: [],
        };
        positionError = (e) => {
            this.setState({[e.target.name]: e.target.value})
          }
      }
    componentDidMount(){

        navigator.geolocation.getCurrentPosition(positionSuccess, positionError);


        fetch("https://cors-anywhere.herokuapp.com/https://www.metaweather.com/api/location/" + this.state.location,{
                method: 'GET',
                headers: {
                  'Accept': 'application/json',
                  'Content-Type': 'application/json',
                },
        })
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                  weather: result
              });
            },
            (error) => {
                return <p>Loading your info....</p>
            }
        )
    }
    render(){
         if(this.state.weather !== undefined){
            var options = { weekday: 'long', month: 'long', day: 'numeric' };
             let localDate = new Date(this.state.weather.time).toLocaleDateString("En-Us", options);
    
   
            return(
                <div className = "weatherInfo">
                    <h1 className="update">Your Local Weather Report</h1>
                    <img style={{width: 175, height: 175}} src={"https://cors-anywhere.herokuapp.com/https://www.metaweather.com/static/img/weather/png/" + this.state.weather.consolidated_weather[0].weather_state_abbr + ".png"} />
                    <p >Hmmm... Weather looks like: {this.state.weather.consolidated_weather[0].weather_state_name}</p>
                    <p >Current Tempature: {Math.round(this.state.weather.consolidated_weather[0].the_temp * 9/5 + 32) } Degrees, Farenheight</p>
                    <p >As Of: {localDate}</p>
                </div>
                
                
            )
            
         }
         return(
             <p>Loading your Weather</p>
         )

    }   

}
function positionSuccess(params) {
    
}

function positionError(params) {
    this.setState({
        location: 2471217
    });
}
export default WeatherReport;
